import React, { useEffect, useState } from 'react';

function App() {
  const [notes, setNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch all notes initially
  useEffect(() => {
    fetch('http://localhost:8080/notes')
      .then(response => response.json())
      .then(data => {
        console.log("Fetched all notes:", data);  // Debugging: Log all notes
        setNotes(data);
      });
  }, []);

  // Handle search functionality
  const handleSearch = async () => {
    console.log("Search query:", searchQuery);  // Debugging: Log search query
    if (searchQuery.trim()) {
      // Search notes based on the query
      fetch(`http://localhost:8080/search?q=${searchQuery}`)
        .then(response => response.json())
        .then(data => {
          console.log("Search Results:", data);  // Debugging: Log search results
          if (data.results && Array.isArray(data.results)) {
            setNotes(data.results);  // Update list with search results
          } else {
            console.error("Search results format is incorrect", data);
          }
          setSelectedNote(null);  // Clear content when search results update
        })
        .catch(error => {
          console.error("Error fetching search results:", error);
        });
    } else {
      // Reset to all notes when search is empty
      fetch('http://localhost:8080/notes')
        .then(response => response.json())
        .then(data => setNotes(data));  // Reset to all notes
    }
  };

  // Fetch note content when a title is clicked
  const fetchNoteContent = (id) => {
    console.log("Fetching content for note ID:", id); // Debugging: Log note ID
    if (id) {
      fetch(`http://localhost:8080/notes/${id}`)
        .then(response => response.json())
        .then(data => {
          console.log("Fetched note:", data);  // Debugging: Log note data
          setSelectedNote(data.content); // Set selected note content
        })
        .catch(error => {
          console.error("Error fetching note content:", error);
        });
    } else {
      console.error("Invalid note ID:", id);  // Debugging: Log invalid ID
    }
  };

  return (
    <div style={{ display: 'flex', padding: '20px' }}>
      <div style={{ width: '300px', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
        <h2>Search Notes</h2>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search notes"
          style={{ width: '100%', padding: '10px' }}
        />
        <button onClick={handleSearch} style={{ padding: '10px', marginTop: '10px' }}>Search</button>

        <h2>Book Titles</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {notes.map(note => (
            <li key={note.note_id} style={{ marginBottom: '10px', cursor: 'pointer' }} onClick={() => fetchNoteContent(note.note_id)}>
              {note.title}
            </li>
          ))}
        </ul>
      </div>

      <div style={{ flex: 1, paddingLeft: '20px' }}>
        <h2>Note Content</h2>
        <div style={{ whiteSpace: 'pre-wrap' }}>
          {selectedNote || "Click on a title to view its content."}
        </div>
      </div>
    </div>
  );
}

export default App;
