import React, { useEffect, useState } from 'react';

function App() {
  const [notes, setNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/notes')
      .then(res => res.json())
      .then(data => {
        setNotes(data);
      })
      .catch(err => console.error('Error fetching notes:', err));
  }, []);

  const fetchNoteContent = (id) => {
    if (!id) {
      console.warn('Invalid note ID passed to fetchNoteContent:', id);
      return;
    }

    fetch(`http://localhost:8080/notes/${id}`)
      .then(res => res.json())
      .then(data => {
        setSelectedNote(data.content);
      })
      .catch(err => console.error('Error fetching note content:', err));
  };

  const handleSearch = () => {
    if (!searchQuery.trim()) {
      setSearchResults([]);
      return;
    }

    fetch(`http://localhost:8080/search?q=${searchQuery}`)
      .then(res => res.json())
      .then(data => {
        setSearchResults(data.results || []);
      })
      .catch(err => console.error('Search error:', err));
  };

  const renderNoteList = (noteList, label) => (
    <>
      <h3>{label}</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {noteList.map(note => (
          <li
            key={note.id}
            onClick={() => fetchNoteContent(note.id)}
            style={{
              cursor: 'pointer',
              marginBottom: '10px',
              padding: '5px',
              border: '1px solid #ccc',
              borderRadius: '6px'
            }}
          >
            {note.title}
          </li>
        ))}
      </ul>
    </>
  );

  return (
    <div style={{ display: 'flex', height: '100vh', padding: '20px' }}>
      {/* Sidebar */}
      <div style={{ width: '300px', overflowY: 'auto', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
        <h2>All Notes</h2>
        {renderNoteList(notes, "All Notes")}

        <div style={{ marginTop: '30px' }}>
          <h2>Search Notes</h2>
          <input
            type="text"
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            placeholder="Enter search query"
            style={{ width: '100%', padding: '8px' }}
          />
          <button onClick={handleSearch} style={{ marginTop: '10px', padding: '8px', width: '100%' }}>
            Search
          </button>
        </div>

        {searchQuery && searchResults.length > 0 && (
          <div style={{ marginTop: '20px' }}>
            {renderNoteList(searchResults, "Search Results")}
          </div>
        )}
      </div>

      {/* Note content area */}
      <div style={{ flex: 1, paddingLeft: '20px', overflowY: 'auto' }}>
        <h2>Note Content</h2>
        <div style={{ whiteSpace: 'pre-wrap' }}>
          {selectedNote || "Click on a note to view its content."}
        </div>
      </div>
    </div>
  );
}

export default App;
